import { SET_GAME } from '../actions/gameActions';

const INITIAL_STATE = {
  game_timer: 0,
  correct_answer: false,
};

const gameReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SET_GAME:
    return action.payload;
  default:
    return state;
  }
};

export default gameReducer;
