import { REQUEST_TOKEN } from '../actions/tokenActions';

const INITIAL_STATE = {
  tokenInfo: {},
};

const tokenReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case REQUEST_TOKEN:
    console.log(action.payload, 'payload');
    return {
      ...state,
      tokenInfo: action.payload.token,
    };
  default:
    return state;
  }
};

export default tokenReducer;
