import { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { setHandleApi, setUserInfo } from '../../action';
import { SCORE_INIT, SCORE_EASY, SCORE_MEDIUM,
  SCORE_HARD } from '../constants/ScreenGame';

export function CheckAnswer({ target }, props) {
  const { player: { name, email, score, total, assertions },
    questions,
    handleApi,
    parameters: { index } } = props;
  const [Interval, setIntervallGame] = useState(30);
  // const [btnDisable, setBtnDisable] = useState(true);
  useEffect(() => {
    handleApi();
    setInterval(() => {
      setIntervallGame((Interval) => Interval - 1);
    }, 1000);
    return () => clearInterval(Interval);
  }, []);

  // useEffect(() => {
  //   if (Interval <= 0) {
  //     setBtnDisable(true);
  //   } else {
  //     setBtnDisable(false);
  //   }
  // }, [Interval]);

  console.log(`Interval - ${Interval} / index - ${index}`);
  let difficulty = 0;
  if (questions[index].difficulty === 'easy') {
    difficulty = SCORE_EASY;
  } else if (questions[index].difficulty === 'medium') {
    difficulty = SCORE_MEDIUM;
  } else if (questions[index].difficulty === 'hard') {
    difficulty = SCORE_HARD;
  }

  const isCorrect = target.getAttribute('data-testid').includes('correct');
  if (isCorrect) {
    const newScore = SCORE_INIT + (Interval * difficulty);
    const userInfo = {
      name,
      email,
      score: score + newScore,
      total: total + newScore,
      assertions: assertions + 1,
    };
    localStorage.setItem('player', JSON.stringify(userInfo));
    props.dispatchUserInfo(userInfo);
  }
}

export function clickMe({ target }) {
  console.log(`Clicou ${target}`);
}

CheckAnswer.propTypes = {
  player: PropTypes.instanceOf(Object),
  dispatchUserInfo: PropTypes.func,
  history: PropTypes.instanceOf(Object),
  questions: PropTypes.instanceOf(Object),
}.isRequired;

const mapStateToProps = (state) => ({
  player: state.player,
  questions: state.questions,
  parameters: state.parameters,
});

const mapDispatchToProps = (dispatch) => ({
  dispatchUserInfo(score) {
    dispatch(setUserInfo(score));
  },
  handleApi() {
    dispatch(setHandleApi());
  },
});

connect(mapStateToProps, mapDispatchToProps)(CheckAnswer);
