import { ADD_TIMER } from '../actions/gameActions';

const INITIAL_STATE = {
  timer: 0,
};

const gameReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case ADD_TIMER:
    return action.payload;
  default:
    return state;
  }
};

export default gameReducer;
