import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { requestToken, setHandleApi, setParameters } from '../action/index';
import '../styles.css';
import { TRINTA, UM, MIL, MAX_QUESTIONS } from '../commons/constants/ScreenGame';

function ScreenGame(props) {
  // const numberRandom = Math.random() * 100;
  const [btnDisable, setBtnDisable] = useState(true);
  const [index, setIndex] = useState(0);
  const [Interval, setIntervallGame] = useState(TRINTA);
  const styleTrue = { border: '3px solid rgb(6, 240, 15)' };
  const styleFalse = { border: '3px solid rgb(255, 0, 0)' };
  const { player: { name, score }, questions, dispatchParameters, handleApi } = props;
  const [countQuestions, setCountQuestions] = useState(1);

  // const parameters = {
  //   btnDisable,
  //   index,
  //   Interval,
  //   styleTrue,
  //   styleFalse,
  //   countQuestions,
  // };
  // dispatchParameters(parameters);

  useEffect(() => {
    handleApi();
    setInterval(() => {
      setIntervallGame((Inter) => Inter - UM);
    }, MIL);
    return () => clearInterval(Interval);
  }, []);

  useEffect(() => {
    if (Interval <= 0) {
      setBtnDisable(true);
    } else {
      setBtnDisable(false);
    }
  }, [Interval]);

  function checkAnswer({ target }) {
    let difficulty = 0;
    if (questions[index].difficulty === 'easy') {
      difficulty = SCORE_EASY;
    } else if (questions[index].difficulty === 'medium') {
      difficulty = SCORE_MEDIUM;
    } else if (questions[index].difficulty === 'hard') {
      difficulty = SCORE_HARD;
    }

    const isCorrect = target.getAttribute('data-testid').includes('correct');
    if (isCorrect) {
      const newScore = SCORE_INIT + (Interval * difficulty);
      const userInfo = {
        name,
        email,
        score: score + newScore,
        total: total + newScore,
        assertions: assertions + 1,
      };
      localStorage.setItem('player', JSON.stringify(userInfo));
      props.dispatchUserInfo(userInfo);
    }
  }

  return (
    <div>
      <header>
        <img
          data-testid="header-profile-picture"
          src="https://www.gravatar.com/avatar/c19ad9dbaf91c5533605fbf985177ccc"
          alt="img"
        />
        <h3 data-testid="header-player-name">{name}</h3>
        <h3 data-testid="header-score">{score}</h3>
      </header>
      {(questions.length > 0) ? (
        <div data-testid="answer-options">
          <p data-testid="question-category">{questions[index].category}</p>
          <p data-testid="question-text">{questions[index].question}</p>

          <section data-testid="answer-options">
            <button
              type="button"
              disabled={ Interval <= 0 ? 'disabled' : btnDisable }
              style={ styleTrue }
              data-testid="correct-answer"
              onClick={ (event) => (checkAnswer(event, props)) }
            >
              {questions[index].correct_answer}
            </button>

            <button
              type="button"
              style={ styleFalse }
              data-testid={ `wrong-answer-${index}` }
            >
              {questions[index].incorrect_answers[0]}
            </button>

            <button
              type="button"
              style={ styleFalse }
              data-testid={ `wrong-answer-${index}` }
            >
              {questions[index].incorrect_answers[1]}
            </button>

            <button
              type="button"
              style={ styleFalse }
              data-testid={ `wrong-answer-${index}` }
            >
              {questions[index].incorrect_answers[2]}
            </button>
          </section>
        </div>
      )
        : '' }
      <button
        data-testid="btn-next"
        onClick={ () => {
          const { history } = props;
          setCountQuestions(countQuestions + 1);
          setIntervallGame(TRINTA);
          setIndex(index + 1);
          if (countQuestions === MAX_QUESTIONS) {
            setCountQuestions(0);
            history.push('/feedback');
          }
        } }
        type="button"
      >
        Proxima Pergunta
      </button>
      <div>
        <p>{`Tempo: ${Interval}`}</p>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  player: state.player,
  questions: state.questions,
});

const mapDispatchToProps = (dispatch) => ({
  RequestToken() {
    dispatch(requestToken());
  },
  handleApi() {
    dispatch(setHandleApi());
  },
  dispatchParameters(parameters) {
    dispatch(setParameters(parameters));
  },
});

ScreenGame.propTypes = {
  player: PropTypes.instanceOf(Object),
  history: PropTypes.instanceOf(Object),
  handleApi: PropTypes.func,
  questions: PropTypes.instanceOf(Object),
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(ScreenGame);
