import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import fetchTriviaApi from '../../services/triviaApi';
import TableApp from '../../components/Table';

function Game(props) {
  const { player: { name, score, gravatarEmail } } = props;
  const [trivia, setTrivia] = useState({
    questions: [],
    index: 0,
    allAnswers: [],
  });
  const [index, setIndex] = useState(0);

  console.log(trivia.questions[index], 'question');

  async function handleTrivia() {
    const fetchTrivia = await fetchTriviaApi();
    setTrivia({
      ...trivia,
      questions: fetchTrivia.results,
    });
  }

  useEffect(() => {
    handleTrivia();
  }, []);

  return (
    <div className="App">
      <header>
        <h1>TRIVIA</h1>
        <TableApp
          name={ name }
          score={ score }
          gravatarEmail={ gravatarEmail }
        />
      </header>
      <main>
        { trivia.questions.length > 0 && (
          <div>
            <p data-testid="question-category">
              { `Category: ${trivia.questions[index].category}` }
            </p>
            <p data-testid="question-text">
              { trivia.questions[index].question }
            </p>
            <div>

            </div>
          </div>
        )}
      </main>
    </div>
  );
}

Game.propTypes = {
  name: PropTypes.string,
  score: PropTypes.number,
}.isRequired;

const mapStateToProps = (state) => ({
  player: state.player,
});

export default connect(mapStateToProps, null)(Game);
