import React from 'react';
import PropTypes from 'prop-types';
import Button from '../../components/Button';
import { MINUS_ONE } from '../../commons/constants';
import './style.css';

function Ranking(props) {
  const { history } = props;
  const ranking = JSON.parse(localStorage.getItem('ranking'));

  return (
    <div className="ranking">
      <section className="ranking__left">
        <div className="ranking__header">
          <h1 data-testid="ranking-title">Ranking</h1>
          <Button
            className="login__btn"
            dataTestid="btn-go-home"
            onClick={ () => history.push('/') }
            text="Home"
            type="button"
          />
        </div>
        <table className="ranking__profile">
          <thead>
            <tr>
              <th>#</th>
              <th className="ranking__profile-picture">Avatar</th>
              <th className="ranking__profile-name">Name</th>
              <th className="ranking__profile-score">Score</th>
            </tr>
          </thead>
          <tbody>
            { ranking.sort((a, b) => a.score > b.score && MINUS_ONE)
              .map(({ picture, name, score }, index) => (
                <tr key={ index }>
                  <td>{ index + 1 }</td>
                  <td className="ranking__profile-picture"><img src={ picture } alt="Player" className="ranking__profile-image" /></td>
                  <td className="ranking__profile-name" data-testid={ `player-name-${index}` }>{ name }</td>
                  <td className="ranking__profile-score" data-testid={ `player-score-${index}` }>{ score }</td>
                </tr>
              ))}
          </tbody>
        </table>
      </section>
      <section className="ranking__right">
        &nbsp;
      </section>
    </div>
  );
}

Ranking.propTypes = {
  history: PropTypes.instanceOf(Object),
}.isRequired;

export default Ranking;
