import React from 'react';
// import ReactPlayer from 'react-player';
import logo from './assets/images/logo.png';
import localVideo from './assets/videos/show.mp4';
import './App.css';
import Login from './pages/Login';

// const extVideo = 'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4';
// const localVideo = 'assets/videos/show.mp4';
// const myVideo = require('./assets/videos/show.mp4');

// console.log(localVideo, 'myVideo');

function App() {
  return (
    <div>
      <header>
        <div className="video-container">
          <video autoPlay muted loop controls>
            <source src="./localVideo.mp4" type="video/mp4" />
          </video>
        </div>
        <section className="login">
          <img src={ logo } className="login__logo" alt="logo" />
          <div className="login__glow">&nbsp;</div>
          <div className="login__form">
            <Login />
          </div>
        </section>
      </header>
    </div>
  );
}

export default App;
