import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { requestToken, handleApi, setUserInfo } from '../action/index';
import '../styles.css';
import {
  TRINTA,
  UM,
  MIL,
  SCORE_INIT,
  SCORE_EASY,
  SCORE_MEDIUM,
  SCORE_HARD,
  MAX_QUESTIONS,
  CINQUENTA,
} from '../commons/constants/ScreenGame';
import checkAnswer from '../commons/utils/ScreenGame';

function ScreenGame(props) {
  const numberRandom = Math.random() * 100;
  const [btnDisable, setBtnDisable] = useState(true);
  const [index, setIndex] = useState(0);
  const [Interval, setIntervallGame] = useState(TRINTA);
  const styleTrue = { border: '3px solid rgb(6, 240, 15)' };
  const styleFalse = { border: '3px solid rgb(255, 0, 0)' };
  const {
    player: { userName, userEmail, score, total, assertions },
    questions,
    dispatchUserInfo,
  } = props;
  const [countQuestions, setCountQuestions] = useState(1);

  const checkAnswerParams = (
    questions,
    index,
    SCORE_INIT,
    SCORE_EASY,
    SCORE_MEDIUM,
    SCORE_HARD,
    userName,
    userEmail,
    score,
    total,
    assertions,
    dispatchUserInfo
  );

  useEffect(() => {
    props.handleApi();
    setInterval(() => {
      setIntervallGame(() => Interval - UM);
    }, MIL);
    return () => clearInterval(Interval);
  }, []);

  useEffect(() => {
    if (Interval <= 0) {
      setBtnDisable(true);
    } else {
      setBtnDisable(false);
    }
  }, [Interval]);

  checkAnswer(checkAnswerParams);

  return (
    <div>
      <header>
        <img
          data-testid="header-profile-picture"
          src="https://www.gravatar.com/avatar/c19ad9dbaf91c5533605fbf985177ccc"
          alt="img"
        />
        <h3 data-testid="header-player-name">{name}</h3>
        <h3 data-testid="header-score">{score}</h3>
      </header>
      {questions.length > 0 && numberRandom <= CINQUENTA ? (
        <div data-testid="answer-options">
          <p data-testid="question-category">{questions[index].category}</p>
          <p data-testid="question-text">{questions[index].question}</p>

          <section data-testid="answer-options">
            <button
              type="button"
              disabled={ Interval <= 0 ? 'disabled' : btnDisable }
              style={ styleTrue }
              data-testid="correct-answer"
              onClick={ (event) => checkAnswer(event) }
            >
              {questions[index].correct_answer}
            </button>

            <button
              type="button"
              style={ styleFalse }
              data-testid={ `wrong-answer-${index}` }
              onClick={ (event) => checkAnswer(event) }
            >
              {questions[index].incorrect_answers[0]}
            </button>

            <button
              type="button"
              style={ styleFalse }
              data-testid={ `wrong-answer-${index}` }
              onClick={ (event) => checkAnswer(event) }
            >
              {questions[index].incorrect_answers[1]}
            </button>

            <button
              type="button"
              style={ styleFalse }
              data-testid={ `wrong-answer-${index}` }
              onClick={ (event) => checkAnswer(event) }
            >
              {questions[index].incorrect_answers[2]}
            </button>
          </section>

        </div>
      ) : (
        questions.length > 0
        && numberRandom > CINQUENTA && (
          <div>
            <p data-testid="question-category">{questions[index].category}</p>
            <p data-testid="question-text">{questions[index].question}</p>

            <section data-testid="answer-options">
              <button
                type="button"
                style={ styleFalse }
                data-testid={ `wrong-answer-${index}` }
                onClick={ (event) => checkAnswer(event) }
              >
                {questions[index].incorrect_answers[0]}
              </button>

              <button
                type="button"
                style={ styleFalse }
                data-testid={ `wrong-answer-${index}` }
                onClick={ (event) => checkAnswer(event) }
              >
                {questions[index].incorrect_answers[1]}
              </button>

              <button
                type="button"
                disabled={ Interval <= 0 ? 'disabled' : btnDisable }
                onClick={ (event) => checkAnswer(event) }
                style={ styleTrue }
                data-testid="correct-answer"
              >
                {questions[index].correct_answer}
              </button>
              <button
                type="button"
                onClick={ (event) => checkAnswer(event) }
                style={ styleFalse }
                data-testid={ `wrong-answer-${index}` }
              >
                {questions[index].incorrect_answers[2]}
              </button>
            </section>
          </div>
        )
      )}
      <button
        data-testid="btn-next"
        onClick={ () => {
          const { history } = props;
          setCountQuestions(countQuestions + 1);
          setIntervallGame(TRINTA);
          setIndex(index + 1);
          if (countQuestions === MAX_QUESTIONS) {
            setCountQuestions(0);
            history.push('/feedback');
          }
        } }
        type="button"
      >
        Proxima Pergunta
      </button>
      <div>
        <p>{`Tempo: ${Interval}`}</p>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  player: state.player,
  questions: state.questions,

});

const mapDispatchToProps = (dispatch) => ({
  RequestToken() {
    dispatch(requestToken());
  },
  dispatchUserInfo(score) {
    dispatch(setUserInfo(score));
  },
  handleApi() {
    dispatch(handleApi());
  },
});

ScreenGame.propTypes = {
  player: PropTypes.instanceOf(Object),
  dispatchUserInfo: PropTypes.func,
  history: PropTypes.instanceOf(Object),
  handleApi: PropTypes.func,
  questions: PropTypes.instanceOf(Object),
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(ScreenGame);
