import React from 'react';
import PropTypes from 'prop-types';
import Table from 'react-bootstrap/Table';
import './style.css';

function TableApp(props) {
  const { className, name, score, gravatarEmail } = props;
  return (
    <Table striped bordered hover variant="dark" size="sm" className={ className }>
      <tbody>
        <tr>
          <td className="game__profile-picture">
            <img
              alt="Profile"
              data-testid="header-profile-picture"
              src={ gravatarEmail }
            />
            <span data-testid="input-gravatar-email">&nbsp;</span>
          </td>
          <td className="game__profile-name"><span data-testid="header-player-name">{ name }</span></td>
          <td className="game__profile-score"><span data-testid="header-score">{ score }</span></td>
        </tr>
      </tbody>
    </Table>
  );
}

TableApp.propTypes = {
  name: PropTypes.string,
  score: PropTypes.number,
  gravatarEmail: PropTypes.string,
}.isRequired;

export default TableApp;
