import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import fetchTriviaApi from '../../services/triviaApi';
import TableApp from '../../components/Table';
import Button from '../../components/Button';
import './style.css';

function Game(props) {
  const { player: { name, score, gravatarEmail } } = props;
  const [questions, setQuestions] = useState([]);
  const [index, setIndex] = useState(0);
  const [answers, setAnswers] = useState({
    correct: '',
    incorrect: [],
    all: [],
  });
  const [disabled, setDisabled] = useState(false);
  const [loading, setLoading] = useState(false);
  const FIFTYCENT = 0.5;

  async function handleTrivia() {
    const fetchTrivia = await fetchTriviaApi();
    setQuestions(fetchTrivia.results);
  }

  function handleAnswers() {
    if (questions.length > 0) {
      const {
        correct_answer: correct,
        incorrect_answers: incorrect,
      } = questions[index];
      // https://flaviocopes.com/how-to-shuffle-array-javascript/
      let all = [correct, ...incorrect];
      all = all.sort(() => Math.random() - FIFTYCENT);
      setAnswers({
        ...answers,
        correct,
        incorrect,
        all,
      });
    }
  }

  function handleClick() {
    console.log('click');
  }

  useEffect(() => {
    handleTrivia();
  }, []);

  useEffect(() => {
    handleAnswers();
  }, [questions]);

  console.log(questions, answers);

  return (
    <div className="App">
      <header>
        <h1>TRIVIA</h1>
        <TableApp
          name={ name }
          score={ score }
          gravatarEmail={ gravatarEmail }
        />
      </header>
      <main>
        { questions.length > 0 && (
          <div>
            <p data-testid="question-category">
              { `Category: ${questions[index].category}` }
            </p>
            <p data-testid="question-text">
              { questions[index].question }
            </p>
            <div>
              { /* // https://flaviocopes.com/how-to-shuffle-array-javascript/ */}
              { answers.all.map((answer, item) => {
                if (answer === answers.correct) {
                  const correctBtn = (
                    <div key={ item }>
                      <Button
                        className="correct"
                        data-testid="correct-answer"
                        disabled={ disabled }
                        onClick={ () => handleClick() }
                        text={ answers.correct }
                        type="button"
                      />
                    </div>
                  );
                  return correctBtn;
                }
              })}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

Game.propTypes = {
  name: PropTypes.string,
  score: PropTypes.number,
}.isRequired;

const mapStateToProps = (state) => ({
  player: state.player,
});

export default connect(mapStateToProps, null)(Game);
