import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import '../../App.css';
import { connect } from 'react-redux';
import fetchTriviaApi from '../../services/triviaApi';
import TableApp from '../../components/Table';

function Game(props) {
  const { player: { name, score, gravatarEmail } } = props;
  const [questions, setQuestions] = useState({});

  async function handleTrivia() {
    const fetchQuestions = await fetchTriviaApi();
    setQuestions(fetchQuestions.results);
  }

  useEffect(() => {
    console.log(handleTrivia());
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>TRIVIA</h1>
        <TableApp
          name={ name }
          score={ score }
          gravatarEmail={ gravatarEmail }
        />
      </header>
      <main>
        { questions.map(({ category }, index) => (
          <div key={ index }>
            <p data-testid="question-category">{`Category: ${category}`}</p>
            <p data-testid="question-text">Question</p>
          </div>
        ))}
      </main>
    </div>
  );
}

Game.propTypes = {
  name: PropTypes.string,
  score: PropTypes.number,
}.isRequired;

const mapStateToProps = (state) => ({
  player: state.player,
});

export default connect(mapStateToProps, null)(Game);
